(77, 'Gối tựa lưng thổ cẩm', 'BGST01', '350000 ', 60, NULL, '1731466895_goitualung.jpg', '<p>Gối tựa lưng thổ cẩm, mềm mại, phong c&aacute;ch truyền thống</p>\r\n\r\n<table>\r\n</table>\r\n\r\n<table>\r\n</table>\r\n', '<p>Gối tựa lưng thổ cẩm được l&agrave;m từ vải mềm mại, c&oacute; họa tiết thổ cẩm đặc sắc, mang đến sự ấm &aacute;p v&agrave; phong c&aacute;ch cho kh&ocirc;ng gian sống của bạn. Ph&ugrave; hợp với ph&ograve;ng kh&aacute;ch, ghế sofa.</p>\r\n\r\n<table>\r\n</table>\r\n\r\n<table>\r\n</table>\r\n', 1, 14),
(79, 'Chả cá Lã Vọng', 'CC001', '220000 ', 40, 1, '1731466765_cha-ca-la-vong.jpg', '<p>Chả c&aacute; L&atilde; Vọng, hương vị thơm ngon, độc đ&aacute;o</p>\r\n\r\n<table>\r\n</table>\r\n\r\n<table>\r\n</table>\r\n', '<p>Chả c&aacute; L&atilde; Vọng được l&agrave;m từ c&aacute; lăng tươi, gia vị đặc trưng v&agrave; tẩm ướp kh&eacute;o l&eacute;o. M&oacute;n ăn n&agrave;y nổi tiếng tại H&agrave; Nội, c&oacute; vị ngọt tự nhi&ecirc;n từ c&aacute;, được nướng v&agrave;ng gi&ograve;n v&agrave; thường ăn k&egrave;m với b&uacute;n, rau sống.</p>\r\n\r\n<table>\r\n</table>\r\n\r\n<table>\r\n</table>\r\n', 1, 15),
(80, 'Túi vải thổ cẩm', 'TVTC001', '150000 ', 50, 1, '1731466671_tuivaitho.jpg', '<p>T&uacute;i vải thổ cẩm truyền thống, bền đẹp, tiện dụng</p>\r\n\r\n<table>\r\n</table>\r\n', '<p>T&uacute;i vải thổ cẩm được l&agrave;m từ chất liệu vải bền, thiết kế độc đ&aacute;o với họa tiết thổ cẩm truyền thống, c&oacute; thể sử dụng h&agrave;ng ng&agrave;y hoặc l&agrave;m qu&agrave; tặng. Mỗi chiếc t&uacute;i đều l&agrave; sản phẩm thủ c&ocirc;ng đầy nghệ thuật.</p>\r\n\r\n<table>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n', 1, 14),
(81, 'Bộ ấm trà gốm sứ Bát Tràng', 'BT001', '350000 ', 30, 1, '1731466602_bộ-bình-xanh-lục-bảo.jpg', '<table>\r\n</table>\r\n\r\n<p>Bộ ấm tr&agrave; gốm sứ B&aacute;t Tr&agrave;ng, họa tiết đẹp mắt</p>\r\n\r\n<table>\r\n</table>\r\n', '<p>Bộ ấm tr&agrave; gốm sứ B&aacute;t Tr&agrave;ng được l&agrave;m thủ c&ocirc;ng, với họa tiết truyền thống, tinh tế, ph&ugrave; hợp để sử dụng trong c&aacute;c buổi tr&agrave; đạo hoặc l&agrave;m qu&agrave; tặng cao cấp. Chất liệu gốm sứ gi&uacute;p tr&agrave; giữ nhiệt l&acirc;u hơn.</p>\r\n', 1, 14),
(82, 'Rượu nếp cái hoa vàng', 'RNN001', '250000 ', 150, NULL, '1731466450_ruou-nep-cai-hoa-vang-chai500ml3.jpg', '<p>Rượu nếp c&aacute;i hoa v&agrave;ng, thơm ngon, chất lượng</p>\r\n', '<p>Rượu nếp c&aacute;i hoa v&agrave;ng được l&agrave;m từ gạo nếp c&aacute;i hoa v&agrave;ng nổi tiếng, ng&acirc;m ủ l&ecirc;n men tự nhi&ecirc;n, c&oacute; vị ngọt thanh, thơm nồng, rất ph&ugrave; hợp trong c&aacute;c dịp lễ hội hay thết đ&atilde;i kh&aacute;ch qu&yacute;.</p>\r\n', 1, 13),
(84, 'Nước mắm Cá Cơm', 'NMCC001', '90000 ', 499, 1, '1731466280_nuoc_mam_ca_com.png', '<p>Nước mắm được l&agrave;m từ c&aacute; cơm nguy&ecirc;n chất</p>\r\n', '<p>Nước mắm c&aacute; cơm được chế biến từ c&aacute; cơm tươi nguy&ecirc;n chất, đảm bảo độ đạm cao v&agrave; vị mặn ngọt tự nhi&ecirc;n. Sản phẩm th&iacute;ch hợp để chế biến c&aacute;c m&oacute;n ăn như gỏi, c&aacute; kho, hoặc l&agrave;m nước chấm.</p>\r\n', 1, 12),
(85, 'Nước mắm Phú Quốc', 'NM1', '120000 ', 195, 5, '1731466177_tour-phú-quốc57.jpg', '<p>Nước mắm Ph&uacute; Quốc đậm đ&agrave;, hương vị đặc trưng</p>\r\n', '<p>Nước mắm Ph&uacute; Quốc được l&agrave;m từ c&aacute; cơm tươi, qua quy tr&igrave;nh l&ecirc;n men tự nhi&ecirc;n, tạo ra sản phẩm nước mắm c&oacute; độ đạm cao v&agrave; hương vị ngọt, đậm đ&agrave;. L&agrave; gia vị kh&ocirc;ng thể thiếu trong c&aacute;c m&oacute;n ăn Việt.</p>\r\n', 1, 12),
(86, 'Nem thính', 'NEMTH001', '180000 ', 96, 4, '1731466100_nem-thinh.png', '<p>Nem th&iacute;nh đậm đ&agrave;, hương vị truyền thống miền Bắc</p>\r\n', '<p>Nem th&iacute;nh được l&agrave;m từ thịt lợn xay, trộn với th&iacute;nh (gạo rang xay) v&agrave; gia vị, l&ecirc;n men tự nhi&ecirc;n. Đ&acirc;y l&agrave; m&oacute;n ăn truyền thống của miền Bắc, c&oacute; vị chua nhẹ, thơm ngon. Ăn k&egrave;m với l&aacute; đinh lăng, chuối xanh.</p>\r\n', 1, 10),
(87, 'Chả quế', 'CHAQ001', '200000', 28, 2, '1731466012_chaque.jpg', '<p>Chả quế được l&agrave;m từ thịt lợn tươi, gia vị đặc trưng</p>\r\n', '<p>Chả quế c&oacute; m&ugrave;i thơm đặc trưng của quế v&agrave; c&aacute;c loại gia vị kh&aacute;c, được l&agrave;m từ thịt lợn tươi, kết hợp với c&aacute;c nguy&ecirc;n liệu tự nhi&ecirc;n tạo n&ecirc;n hương vị đặc biệt. Thường được ăn c&ugrave;ng với cơm hoặc b&uacute;n.</p>\r\n', 1, 15),
(88, 'Nem rán Hà Nội', 'NEMHN001', '150000 ', 50, 4, '1731465842_nem-ran-ha-noi.jpg', '<p>Nem r&aacute;n truyền thống, gi&ograve;n thơm, nh&acirc;n thịt heo, mộc nhĩ</p>\r\n', '<p>Nem r&aacute;n được cuốn bằng lớp b&aacute;nh đa mỏng, nh&acirc;n thịt heo xay, mộc nhĩ, nấm hương, gia vị đặc trưng, chi&ecirc;n gi&ograve;n. Đặc sản nổi tiếng của H&agrave; Nội, th&iacute;ch hợp ăn k&egrave;m với rau sống v&agrave; nước chấm.</p>\r\n', 1, 10);

